import { initStore } from './store';

const configureStore = () => {
    const actions = {
        ADD: (state, amount) => ({
            seconds: state.seconds + amount,
            minutes: parseInt(state.seconds /60),
            hours: parseInt((state.seconds /60)/60),
            renderedSeconds: (parseInt(state.seconds)-(parseInt(state.minutes)*60)),
            renderedMinutes: (parseInt(state.minutes)-(parseInt(state.hours)*60))}

            ),
        SUB: (state, amount) => ({seconds: state.seconds - amount}),
    };

    initStore(actions, { seconds: 0 });
};

export default configureStore;
