import { initStore } from './store';

const configureStore = () => {
    const actions = {
        ADD: (state, amount, isPlay) => ({

            seconds: isPlay ? state.seconds + amount : state.seconds,
            renderedSeconds: state.seconds % 60,
            minutes: state.seconds === 59 ? state.minutes+amount : state.minutes,
            hours: state.minutes === 60 && state.renderedSeconds === 59 ? state.hours+amount : state.hours,
            renderedMinutes: state.minutes % 60,
            isPlay: state.isPlay

        }
            ),
        PLAY: (state, isPlay) => ({
            seconds: state.seconds,
            renderedSeconds: state.renderedSeconds,
            minutes: state.minutes,
            hours: state.hours,
            renderedMinutes: state.renderedMinutes,
            isPlay: !state.isPlay}),
    };

    initStore(actions, { seconds: 0, minutes: 0, hours: 0, isPlay: false });
};

export default configureStore;
