import { initStore } from './store';

const configureStore = () => {
    const actions = {
        ADD: (state, amount, timeStamp) => ({
            seconds: isPlay && state.seconds + amount,
            renderedSeconds: state.seconds % 60,
            minutes: state.seconds === 59 ? state.minutes+amount : state.minutes,
            hours: state.minutes === 60 && state.renderedSeconds === 59 ? state.hours+amount : state.hours,
            renderedMinutes: state.minutes % 60,
            isPlay: state.isPlay
        }
            ),
        play: (state, amount, timeStamp) => ({isPlay: true }),
    };

    initStore(actions, { seconds: 0, minutes: 0, hours: 0, isPlay: true });
};

export default configureStore;
