import { initStore } from './store';

const configureStore = () => {
    const actions = {
        ADD: (state, amount) => ({
            seconds: state.seconds + amount,
            minutes: state.seconds === 59 ? state.minutes+amount : state.minutes,
            hours: state.minutes === 60 && state.seconds % 60 === 0 ? state.hours+amount : state.hours,
            renderedSeconds: state.seconds % 60,
            renderedMinutes: state.minutes % 60
        }
            ),
        SUB: (state, amount) => ({seconds: state.seconds - amount}),
    };

    initStore(actions, { seconds: 57, minutes: 59, hours: 0 });
};

export default configureStore;
