import React, { useEffect, useState } from "react";

import { useStore } from "../hooks-store/store";
import "./Counter.css";

const Counter = (props) => {
  const [state, dispatch] = useStore();
  const [timeStamp, setTimeStamp] = useState(null);

  useEffect(() => {
    let interval = null;


      interval = setInterval(() => {
         dispatch("ADD", 1, state.isPlay);        
      }, 1000);


    return () => clearInterval(interval);
  }, []);

  const playHandler = () => {
    setTimeStamp(Date.now());
  };

  return (
    <div className="counter">
      <p>Counter</p>( {timeStamp} {state.isPlay})
      <p>
        {" "}
        {state.hours}h{" "}
        {state.renderedMinutes === 60 ? 0 : state.renderedMinutes}m{" "}
        {state.renderedSeconds === 60 ? 0 : state.renderedSeconds}s
      </p>
      {!state.isPlay && <button onClick={() => dispatch("PLAY")}>Play</button>}
      {state.isPlay && <button onClick={() => dispatch("PAUSE")}>PAUSE</button>}
    </div>
  );
};

export default Counter;
