import { initStore } from './store';

const configureStore = () => {
    const actions = {
        ADD: (state, amount, timeStamp) => ({
            timeStamp: state.timeStamp,
            seconds: state.isPlay ? parseInt((Date.now() - state.timeStamp)/1000) : state.seconds,
            renderedSeconds: state.seconds % 60,
            minutes: parseInt(state.seconds/60),
            hours: state.minutes === 60 && state.renderedSeconds === 59 ? state.hours+amount : state.hours,
            renderedMinutes: state.minutes % 60,
            isPlay: state.isPlay,
            isPaused: state.isPaused

        }
            ),
        PLAY: (state) => ({
            timeStamp: Date.now()-51200,
            seconds: 0,
            renderedSeconds: 0,
            minutes: 0,
            hours: 0,
            renderedMinutes: 0,
            isPlay: true}),
            isPaused: false,
        PAUSE: (state) => ({
            timeStamp: state.timeStamp,
            seconds: state.seconds,
            renderedSeconds: state.renderedSeconds,
            minutes: state.minutes,
            hours: state.hours,
            renderedMinutes: state.renderedMinutes,
            isPlay: false,
            isPaused: true,}),        
        UNPAUSE: (state) => ({
            timeStamp: state.timeStamp,
            seconds: state.seconds,
            renderedSeconds: state.renderedSeconds,
            minutes: state.minutes,
            hours: state.hours,
            renderedMinutes: state.renderedMinutes,
            isPlay: true,
            isPaused: true,}),  
    };


    initStore(actions, { seconds: 0, minutes: 0, hours: 0, isPlay: false });
};

export default configureStore;
