import React, { useEffect, useState } from "react";

import { useStore } from "../hooks-store/store";
import "./Timer.css";

const Timer = (props) => {
  const [state, dispatch] = useStore();
  const [isLoading, setIsLoading] = useState();
  const [httpError, setHttpError] = useState();
  const [timers, setTimers] =useState([]);


  useEffect(() => {
    let interval = null;


      interval = setInterval(() => {
         dispatch("ADD", 1, state.timeStamp);        
      }, 1000);


    return () => clearInterval(interval);
  }, [dispatch, state.timeStamp]);


  const inputHandler = (event) => {
    dispatch("SAVETICKETNUMBER", event.target.value);
  }
  const  sendHandler = async () => {
    const response = await fetch('https://react-http-340ad-default-rtdb.europe-west1.firebasedatabase.app/timer.json', { 
      method: 'POST',
      body: JSON.stringify({
        seconds: state.seconds,
        ticket: state.ticketNumber,
        timeStamp: state.timeStamp,
      })
   });
  }

  const fetchHandler =() => {
    setIsLoading(true);
    const fetchTimers = async () => {
      const response = await fetch(
        'https://react-http-340ad-default-rtdb.europe-west1.firebasedatabase.app/timer.json'
      );

      if(!response.ok){
        throw new Error('Something went wrong!');
      }

      const responseData = await response.json();
      const loadedTimers = [];
      for (const key in responseData) {
        loadedTimers.push({
          id: key,
          seconds: responseData[key].seconds,
          ticket: responseData[key].ticket
        });
      }
      let temp = "";        
      for(let timer of loadedTimers){

        temp += timer.ticket +": ";
        temp += (parseInt(timer.seconds/60/60))+" hours, "
        temp += (parseInt(timer.seconds/60))+" minutes, "
        temp += timer.seconds+ "seconds | ";
      }

            setTimers(temp);  
      setIsLoading(false);
    };

    fetchTimers().catch(error => {
      setIsLoading(false);
    setHttpError(error.message);
    });
  };


  return (
    <div className="counter">
      <p>Tiketti: <input type="text" onChange={inputHandler}></input></p>
      <p>Counter</p>( {state.timeStamp} {state.isPlay})

      <div>
      {state.ticketNumber}:   {state.hours}h{" "}
        {state.renderedMinutes === 60 ? 0 : state.renderedMinutes}m{" "}
        {state.renderedSeconds === 60 ? 0 : state.renderedSeconds}s
      </div>

      {!state.isPlay && <button onClick={() => dispatch("PLAY")}>{state.isPaused? 'Restart' : 'Play'}</button>}
      {state.isPlay && <button onClick={() => dispatch("PAUSE")}>PAUSE</button>}
      {state.isPaused && !state.isPlay && <button onClick={() => dispatch("UNPAUSE")}>UNPAUSE</button>}
      <button onClick={sendHandler}>SEND</button>
      <button onClick={fetchHandler}>FETCH</button>

      <br />timer is: {timers.toString()}

    </div>
  );
};

export default Timer;
