import React, {useEffect, useState} from 'react';

import { useStore } from '../hooks-store/store';
import './Counter.css';

const Counter = props => {
  const [state, dispatch] = useStore();
  const [isPaused, setIsPaused] = useState(false);
  const [isStarted, setisStarted] = useState(true);

  useEffect(() => {
    let interval = null;
    if (isStarted && !isPaused) {
      interval = setInterval(() => {
        dispatch('ADD', 1)
      }, 1000);
    } else if (isPaused && seconds !== 0) {
      clearInterval(interval);
    }

    return () => clearInterval(interval);
  }, []);
  const pauseHandler= () => {
    setIsPaused(!isPaused);
  }

  const playHandler= () => {
    setisStarted(!isStarted);
  }


  return (
    <div className="counter">
      <p>Only there to proof, that you can have multiple state slices.</p>
      <p> {parseInt(state.hours)}h {parseInt(state.minutes)}m {state.seconds}s</p>
      <button onClick={() => dispatch('ADD', 1)}>Add 1</button>
      <button onClick={() => dispatch('ADD', 5)}>Add 5</button>
      <button onClick={playHandler}>Play</button>
      <button onClick={pauseHandler}>Pause</button>
    </div>
  );
};

export default Counter;
